import React, { useEffect, useState } from 'react';
import TopNav from '../components/TopNav';
import {
  Container,
  Card,
  Row,
  Col,
  Badge,
  Spinner,
  ListGroup,
} from 'react-bootstrap';
import { getStudent, predictSingle } from '../services/api';
import { useParams } from 'react-router-dom';

export default function StudentProfile() {
  const { id } = useParams();
  const [s, setS] = useState(null);
  const [pred, setPred] = useState(null);
  const [loading, setLoading] = useState(true);

  const buildPayloadFromStudent = (stu) => {
    const { id, ...rest } = stu;
    return rest;
  };

  useEffect(() => {
    const fetchData = async () => {
      if (!id) return;
      try {
        const stu = await getStudent(id);
        setS(stu);

        // call ML for this single student
        try {
          const payload = buildPayloadFromStudent(stu);
          const p = await predictSingle(payload);
          setPred({
            dropout_probability: Number(p.dropout_probability),
            deservingness_score: Number(p.deservingness_score),
            risk_tier: p.risk_tier,
          });
        } catch (e) {
          console.error('Prediction error', e);
          setPred(null);
        }
      } catch (e) {
        console.error(e);
        setS(null);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) {
    return (
      <>
        <TopNav />
        <Container className="p-4">
          <Card className="p-4 text-center shadow-sm">
            <Spinner animation="border" className="me-2" />
            Loading student profileâ€¦
          </Card>
        </Container>
      </>
    );
  }

  if (!s) {
    return (
      <>
        <TopNav />
        <Container className="p-4">
          <Card className="p-4 text-center shadow-sm">
            Unable to load student profile.
          </Card>
        </Container>
      </>
    );
  }

  const riskVariant =
    pred?.risk_tier === 'HIGH'
      ? 'danger'
      : pred?.risk_tier === 'MEDIUM'
      ? 'warning'
      : 'success';

  return (
    <>
      <TopNav />
      <div
        style={{
          minHeight: '100vh',
          background:
            'linear-gradient(135deg, #111827 0%, #020617 40%, #020617 100%)',
        }}
      >
        <Container className="py-4">
          <Row className="mb-3">
            <Col>
              <h3 className="text-light mb-1">Student Profile</h3>
              <small className="text-muted">
                Detailed view with dropout-risk explanation
              </small>
            </Col>
          </Row>

          <Row className="g-4">
            {/* LEFT: SUMMARY CARD */}
            <Col md={4}>
              <Card className="shadow-lg border-0">
                <Card.Body>
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <div>
                      <h4 className="mb-0">{s.name}</h4>
                      <small className="text-muted">{s.district}</small>
                    </div>
                    {pred && (
                      <Badge bg={riskVariant} pill>
                        {pred.risk_tier} risk
                      </Badge>
                    )}
                  </div>

                  <ListGroup variant="flush" className="mt-3 small">
                    <ListGroup.Item>
                      <strong>School Type:</strong>{' '}
                      {s.private_or_govt_school || 'N/A'}
                    </ListGroup.Item>
                    <ListGroup.Item>
                      <strong>Family Income:</strong>{' '}
                      {s.family_income ?? s.familyIncome ?? 'N/A'}
                    </ListGroup.Item>
                    <ListGroup.Item>
                      <strong>Family Members:</strong>{' '}
                      {s.family_members ?? s.familyMembers ?? 'N/A'}
                    </ListGroup.Item>
                    <ListGroup.Item>
                      <strong>Scholarship:</strong>{' '}
                      {s.any_scholarship || 'N/A'}
                    </ListGroup.Item>
                    <ListGroup.Item>
                      <strong>First Graduate:</strong>{' '}
                      {s.first_graduate || 'N/A'}
                    </ListGroup.Item>
                  </ListGroup>
                </Card.Body>
              </Card>
            </Col>

            {/* RIGHT: DETAILS + RISK */}
            <Col md={8}>
              <Row className="g-3">
                <Col md={6}>
                  <Card className="shadow-sm h-100">
                    <Card.Body>
                      <Card.Title>Academic Snapshot</Card.Title>
                      <div className="mt-3">
                        <p className="mb-1">
                          <strong>Academic Score:</strong>{' '}
                          {s.academic_score ?? s.academicScore ?? '-'}
                        </p>
                        <p className="mb-1">
                          <strong>Motivation Level:</strong>{' '}
                          {s.motivation_level ?? s.motivationLevel ?? '-'}
                        </p>
                        <p className="mb-1">
                          <strong>Attendance Rate:</strong>{' '}
                          {s.attendance_rate ?? s.attendanceRate ?? '-'}%
                        </p>
                        <p className="mb-1">
                          <strong>Extra-curricular:</strong>{' '}
                          {s.extra_curricular || 'N/A'}
                        </p>
                        <p className="mb-1">
                          <strong>Attitude:</strong> {s.attitude || 'N/A'}
                        </p>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6}>
                  <Card className="shadow-sm h-100">
                    <Card.Body>
                      <Card.Title>Risk & Support</Card.Title>
                      {pred ? (
                        <>
                          <p className="mb-1">
                            <strong>Dropout Probability:</strong>{' '}
                            {(pred.dropout_probability * 100).toFixed(1)}%
                          </p>
                          <p className="mb-1">
                            <strong>Deservingness Score:</strong>{' '}
                            {pred.deservingness_score.toFixed(2)}
                          </p>
                          <p className="mb-2">
                            <strong>Family Support:</strong>{' '}
                            {s.family_support || 'N/A'}
                          </p>
                          <p className="small text-muted">
                            This risk label is generated by your trained ML
                            model using marks, income, motivation, attendance
                            and family-support features.
                          </p>
                        </>
                      ) : (
                        <p className="text-muted">
                          Prediction not available for this student.
                        </p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </Col>
          </Row>
        </Container>
      </div>
    </>
  );
}
