// src/components/TopNav.jsx
import React, { useState, useEffect } from "react";
import { Navbar, Container, Nav, Button } from "react-bootstrap";
import { useNavigate, useLocation } from "react-router-dom";

/**
 * Modern, animated Navbar with gradient background, dynamic hover effects,
 * scroll-responsive shadow, and context highlighting based on the current
 * route. Matches the premium UI style used on PredictSingle and Dashboard pages.
 *
 * No external libs needed other than Bootstrap & React Router.
 */

export default function TopNav() {
  const nav = useNavigate();
  const location = useLocation();

  // State to manage scroll shadow effect
  const [isScrolled, setIsScrolled] = useState(false);

  // Add a scroll listener to window
  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 10);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Logout handler
  const logout = () => {
    localStorage.removeItem("deserveiq_user");
    nav("/login");
  };

  // Helper: is the current path matching?
  const isActive = (path) => location.pathname.startsWith(path);

  // Dynamic styling for nav links based on active status
  const navLinkStyle = (active) => ({
    color: active ? "#fff" : "rgba(255,255,255,0.8)",
    fontWeight: active ? 700 : 500,
    fontSize: "0.9rem",
    letterSpacing: "0.3px",
    transition: "all 0.2s ease",
    padding: "8px 12px",
    borderRadius: 10,
    backgroundColor: active ? "rgba(255,255,255,0.18)" : "transparent",
  });

  return (
    <Navbar
      expand="lg"
      fixed="top"
      style={{
        background: `linear-gradient(90deg, #4f46e5 0%, #6366f1 50%, #8b5cf6 100%)`,
        transition: "all 0.3s ease",
        padding: "0.6rem 0",
        boxShadow: isScrolled
          ? "0 4px 12px rgba(0, 0, 0, 0.15)"
          : "0 1px 4px rgba(0, 0, 0, 0.04)",
        fontFamily: '"Inter", sans-serif',
        zIndex: 9999,
      }}
    >
      <Container fluid style={{ maxWidth: "1200px" }}>
        {/* BRAND */}
        <Navbar.Brand
          onClick={() => nav("/")}
          style={{
            cursor: "pointer",
            color: "white",
            fontWeight: 800,
            fontSize: "1.4rem",
            letterSpacing: "0.4px",
            display: "flex",
            alignItems: "center",
            gap: "8px",
          }}
        >
          <span role="img" aria-label="brain">
            üß†
          </span>
          DeserveIQ
        </Navbar.Brand>

        {/* TOGGLE */}
        <Navbar.Toggle
          aria-controls="main-nav"
          style={{ border: "none", color: "white" }}
        />

        {/* NAV ITEMS */}
        <Navbar.Collapse id="main-nav" className="justify-content-end">
          <Nav
            className="align-items-center"
            style={{ gap: "8px", transition: "all 0.3s ease" }}
          >
            {[
              { path: "/predict", label: "Single Predict", icon: "üéØ" },
              { path: "/batch", label: "Batch Upload", icon: "üì§" },
              { path: "/students", label: "Students", icon: "üßæ" },
            ].map(({ path, label, icon }) => (
              <Nav.Link
                key={path}
                onClick={() => nav(path)}
                style={navLinkStyle(isActive(path))}
                onMouseEnter={(e) =>
                  !isActive(path) &&
                  (e.target.style.backgroundColor = "rgba(255,255,255,0.12)")
                }
                onMouseLeave={(e) =>
                  !isActive(path) &&
                  (e.target.style.backgroundColor = "transparent")
                }
              >
                <span style={{ fontSize: "1.1rem", marginRight: 6 }}>{icon}</span>
                {label}
              </Nav.Link>
            ))}

            <Button
              size="sm"
              onClick={logout}
              style={{
                background: "white",
                color: "#b91c1c",
                fontWeight: 700,
                border: "none",
                borderRadius: 10,
                padding: "8px 14px",
                letterSpacing: "0.3px",
                fontSize: "0.9rem",
                boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)",
                transition: "all 0.3s ease",
              }}
              onMouseEnter={(e) => (e.target.style.transform = "scale(1.05)")}
              onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
            >
              ‚ùå Logout
            </Button>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
