import React, { useEffect, useState } from "react";
import TopNav from "../components/TopNav";
import { Container, Row, Col, Card, Badge, Spinner, Table } from "react-bootstrap";
import { getStudents, predictSingle } from "../services/api";

export default function Dashboard() {
  const [students, setStudents] = useState([]);
  const [preds, setPreds] = useState([]);
  const [loading, setLoading] = useState(true);

  const buildPayload = (s) => {
    const { id, ...rest } = s;
    return rest;
  };

  useEffect(() => {
    (async () => {
      try {
        const data = await getStudents();
        const arr = Array.isArray(data) ? data : [];
        setStudents(arr);

        const subset = arr.slice(0, 30);

        const enriched = await Promise.all(
          subset.map(async (s) => {
            try {
              const p = await predictSingle(buildPayload(s));
              return {
                ...s,
                dropout_probability: Number(p.dropout_probability),
                deservingness_score: Number(p.deservingness_score),
                risk_tier: p.risk_tier,
              };
            } catch {
              return { ...s, dropout_probability: 0, deservingness_score: 0, risk_tier: "LOW" };
            }
          })
        );

        setPreds(enriched);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const avg = (key) =>
    preds.length
      ? Math.round(preds.reduce((a, b) => a + (Number(b[key]) || 0), 0) / preds.length)
      : 0;

  const high = preds.filter((p) => p.risk_tier === "HIGH").length;
  const med = preds.filter((p) => p.risk_tier === "MEDIUM").length;
  const low = preds.filter((p) => p.risk_tier === "LOW").length;

  return (
    <>
      <TopNav />

      <Container className="py-4">

        <h2 className="fw-bold mb-4">ðŸ“Š DeserveIQ Dashboard</h2>

        <Row className="g-3 mb-4">
          <Col md={3}>
            <Card className="p-3 shadow-sm text-center">
              <h6 className="text-muted">Students</h6>
              <h2>{students.length}</h2>
              <Badge bg="primary">Total</Badge>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="p-3 shadow-sm text-center">
              <h6 className="text-muted">At-Risk</h6>
              <h2>{high}</h2>
              <Badge bg="danger">High Risk</Badge>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="p-3 shadow-sm text-center">
              <h6 className="text-muted">Avg Deserving</h6>
              <h2>{avg("deservingness_score")}</h2>
              <Badge bg="success">Score</Badge>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="p-3 shadow-sm text-center">
              <h6 className="text-muted">Avg Attendance</h6>
              <h2>{avg("attendance_rate")}%</h2>
              <Badge bg="info">Attendance</Badge>
            </Card>
          </Col>
        </Row>

        <Card className="shadow-sm p-3">
          <h5 className="fw-bold mb-3">ðŸ”¥ Top Risk Students</h5>

          {loading ? (
            <div className="text-center py-4">
              <Spinner animation="border" />
            </div>
          ) : (
            <Table hover responsive className="align-middle">
              <thead className="table-light">
                <tr>
                  <th>#</th>
                  <th>Name</th>
                  <th>Academic</th>
                  <th>Attendance</th>
                  <th>Deserving</th>
                  <th>Dropout %</th>
                  <th>Risk</th>
                </tr>
              </thead>

              <tbody>
                {preds.map((p, i) => (
                  <tr key={i}>
                    <td>{i + 1}</td>
                    <td className="fw-semibold">{p.name}</td>
                    <td>{p.academic_score}</td>
                    <td>{p.attendance_rate}%</td>
                    <td>{p.deservingness_score.toFixed(2)}</td>
                    <td>{(p.dropout_probability * 100).toFixed(1)}%</td>
                    <td>
                      <Badge
                        bg={
                          p.risk_tier === "HIGH"
                            ? "danger"
                            : p.risk_tier === "MEDIUM"
                            ? "warning"
                            : "success"
                        }
                      >
                        {p.risk_tier}
                      </Badge>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Card>
      </Container>
    </>
  );
}
