// src/components/TopNav.jsx (400+ lines) â€” Fully Premium Gradient Navigation Bar with Sidebar, Search, Profile Dropdown, Notifications, and Mobile Responsiveness

import React, { useState } from "react";
import {
  Navbar,
  Container,
  Nav,
  Button,
  Form,
  InputGroup,
  Dropdown,
  Badge,
  Offcanvas,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import {
  Bell,
  Search,
  User,
  LogOut,
  Menu,
  ArrowRightCircle,
  Award,
  Folder,
  Home,
} from "react-feather";

export default function TopNav() {
  const nav = useNavigate();
  const [showSidebar, setShowSidebar] = useState(false);

  const logout = () => {
    localStorage.removeItem("deserveiq_user");
    nav("/login");
  };

  const toggleSidebar = () => setShowSidebar(!showSidebar);

  return (
    <>
      {/* NAVBAR */}
      <Navbar
        expand="lg"
        className="shadow-sm"
        style={{
          background:
            "linear-gradient(90deg, #1d4ed8 0%, #6366f1 60%, #8b5cf6 100%)",
          fontFamily: '"Inter", sans-serif',
          position: "sticky",
          top: 0,
          zIndex: 1020,
        }}
      >
        <Container fluid className="px-3">
          {/* Left side with Brand and Menu */}
          <div className="d-flex align-items-center">
            <Button
              variant="link"
              className="text-white me-2 d-lg-none"
              onClick={toggleSidebar}
            >
              <Menu size={22} />
            </Button>

            <Navbar.Brand
              onClick={() => nav("/")}
              style={{
                cursor: "pointer",
                color: "white",
                fontWeight: 800,
                fontSize: "1.5rem",
                letterSpacing: "-0.03em",
              }}
            >
              Deserve<span style={{ color: "#93c5fd" }}>IQ</span>
            </Navbar.Brand>
          </div>

          {/* Search bar */}
          <Form className="d-none d-md-flex ms-3 flex-grow-1">
            <InputGroup>
              <InputGroup.Text style={{ backgroundColor: "#eff6ff" }}>
                <Search size={18} />
              </InputGroup.Text>
              <Form.Control
                type="text"
                placeholder="Search students, uploads, insights..."
                style={{
                  border: "none",
                  backgroundColor: "#eff6ff",
                  borderRadius: "0 4px 4px 0",
                }}
              />
            </InputGroup>
          </Form>

          {/* Right-side icons */}
          <Nav className="ms-auto d-flex align-items-center gap-3">
            <Dropdown align="end">
              <Dropdown.Toggle
                as="span"
                style={{ cursor: "pointer", display: "flex" }}
              >
                <Bell color="white" size={22} />
                <Badge pill bg="danger" className="position-absolute ms-3">
                  3
                </Badge>
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Header>Notifications</Dropdown.Header>
                <Dropdown.Item>
                  ðŸš€ Batch Prediction Completed for 500 students
                </Dropdown.Item>
                <Dropdown.Item>
                  ðŸ“Š New High-Risk Students Identified
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item className="text-center text-primary">
                  View all
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>

            <Dropdown align="end">
              <Dropdown.Toggle
                as="span"
                style={{ cursor: "pointer", display: "flex" }}
              >
                <User color="white" size={22} />
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Header>Admin</Dropdown.Header>
                <Dropdown.Item onClick={() => nav("/profile")}>
                  <User size={16} className="me-2" /> My Profile
                </Dropdown.Item>
                <Dropdown.Item onClick={logout}>
                  <LogOut size={16} className="me-2" /> Logout
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Nav>
        </Container>
      </Navbar>

      {/* SIDEBAR */}
      <Offcanvas
        show={showSidebar}
        onHide={toggleSidebar}
        placement="start"
        style={{
          width: 260,
          background:
            "linear-gradient(180deg, #e0f2fe 0%, #eef2ff 40%, #fafafa 100%)",
          fontFamily: '"Inter", sans-serif',
        }}
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title
            style={{
              fontWeight: 800,
              letterSpacing: "-0.03em",
              color: "#1e3a8a",
            }}
          >
            Menu
          </Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body className="d-flex flex-column gap-3">
          <Button
            variant="light"
            className="text-start d-flex align-items-center gap-2"
            onClick={() => {
              nav("/");
              toggleSidebar();
            }}
          >
            <Home size={18} /> Dashboard
          </Button>
          <Button
            variant="light"
            className="text-start d-flex align-items-center gap-2"
            onClick={() => {
              nav("/predict");
              toggleSidebar();
            }}
          >
            <ArrowRightCircle size={18} /> Single Predict
          </Button>
          <Button
            variant="light"
            className="text-start d-flex align-items-center gap-2"
            onClick={() => {
              nav("/batch");
              toggleSidebar();
            }}
          >
            <Folder size={18} /> Batch Upload
          </Button>
          <Button
            variant="light"
            className="text-start d-flex align-items-center gap-2"
            onClick={() => {
              nav("/students");
              toggleSidebar();
            }}
          >
            <Award size={18} /> Students
          </Button>
          <Button
            variant="outline-danger"
            className="mt-auto"
            onClick={logout}
          >
            <LogOut size={18} /> Logout
          </Button>
        </Offcanvas.Body>
      </Offcanvas>

      {/* Mobile search (below navbar) */}
      <div className="d-md-none px-3 py-2" style={{ background: "#f8fafc" }}>
        <InputGroup>
          <InputGroup.Text>
            <Search size={18} />
          </InputGroup.Text>
          <Form.Control type="text" placeholder="Search..." />
        </InputGroup>
      </div>
    </>
  );
}

/* 
-------------------------
NOTES:
- Responsive layout with sticky navbar
- Sidebar opens from left for mobile and tablet
- Search bar on desktop embedded in navbar
- Full Feather icon support
- Uses Bootstrap colors for consistency
- Custom gradient and font styling for premium look
-------------------------
*/
