import React, { useEffect, useState } from "react";
import TopNav from "../components/TopNav";
import { Container, Card, Table, Form } from "react-bootstrap";
import { getStudents } from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Students() {
  const [rows, setRows] = useState([]);
  const [query, setQuery] = useState("");
  const nav = useNavigate();

  useEffect(() => {
    getStudents().then((d) => setRows(Array.isArray(d) ? d : []));
  }, []);

  const filtered = rows.filter(
    (s) =>
      s.name?.toLowerCase().includes(query.toLowerCase()) ||
      s.district?.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <>
      <TopNav />

      <Container className="py-4">
        <h3 className="fw-bold mb-4">ðŸŽ“ Students Registry</h3>

        <Form.Control
          placeholder="Search by name or district..."
          className="mb-3"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />

        <Card className="shadow-sm p-3">
          <Table hover responsive>
            <thead>
              <tr>
                <th>#</th>
                <th>Name</th>
                <th>District</th>
                <th>Academic</th>
                <th>Action</th>
              </tr>
            </thead>

            <tbody>
              {filtered.map((s, i) => (
                <tr key={i}>
                  <td>{i + 1}</td>
                  <td className="fw-semibold">{s.name}</td>
                  <td>{s.district}</td>
                  <td>{s.academic_score ?? "-"}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => nav(`/students/${s.id}`)}
                    >
                      View
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card>
      </Container>
    </>
  );
}
