import React, {useState} from 'react'
import TopNav from '../components/TopNav'
import { Container, Row, Col, Card, Form, Button, Table } from 'react-bootstrap'
import { predictBatch } from '../services/api'
import Papa from 'papaparse'
import { toast } from 'react-toastify'

export default function BatchUpload(){
  const [file, setFile] = useState(null)
  const [preview, setPreview] = useState([])
  const [headers, setHeaders] = useState([])

  const loadFile = (f) => {
    setFile(f)
    Papa.parse(f, {
      header: true, preview: 10,
      complete: (res)=> {
        setHeaders(res.meta.fields || [])
        setPreview(res.data.slice(0,10))
      }
    })
  }

  const upload = async()=>{
    if(!file) return toast.warn('Choose CSV first')
    try{
      const res = await predictBatch(file) // returns blob
      const blob = res.data || res
      const url = window.URL.createObjectURL(blob)
      const a = document.createElement('a')
      a.href = url
      a.download = 'predictions.csv'
      document.body.appendChild(a)
      a.click()
      a.remove()
      toast.success('Batch scored — download started')
    }catch(e){ toast.error('Batch predict failed') }
  }

  return (
    <>
      <TopNav/>
      <Container className="p-4">
        <Row>
          <Col md={4}>
            <Card className="p-3 shadow-sm">
              <h5>Upload CSV</h5>
              <Form.Group className="mb-3">
                <Form.Control type="file" accept=".csv" onChange={e=>loadFile(e.target.files[0])}/>
              </Form.Group>
              <Button onClick={upload}>Upload & Score</Button>
            </Card>
          </Col>
          <Col md={8}>
            <Card className="p-3 shadow-sm">
              <h5>CSV Preview</h5>
              {preview.length===0 ? <p className="text-muted">No preview</p> :
                <Table size="sm" responsive>
                  <thead><tr>{headers.map(h=> <th key={h}>{h}</th>)}</tr></thead>
                  <tbody>{preview.map((r,i)=> <tr key={i}>{headers.map(h=> <td key={h}>{r[h]}</td>)}</tr>)}</tbody>
                </Table>
              }
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  )
}
