import React, {useState} from 'react'
import { Container, Row, Col, Card, Form, Button } from 'react-bootstrap'
import { useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify'

export default function Login(){
  const [email,setEmail]=useState('admin@maatram.org')
  const [pw,setPw]=useState('password')
  const nav = useNavigate()

  const submit = (e)=> {
    e.preventDefault()
    // simple local auth for demo
    if(email && pw){
      localStorage.setItem('deserveiq_user', JSON.stringify({email}))
      toast.success('Logged in')
      nav('/')
    } else toast.error('Enter credentials')
  }

  return (
    <Container className="vh-100 d-flex align-items-center">
      <Row className="w-100">
        <Col md={{span:6, offset:3}}>
          <Card className="p-4 shadow-lg">
            <h3 className="mb-3">DeserveIQ â€” Login</h3>
            <Form onSubmit={submit}>
              <Form.Group className="mb-2">
                <Form.Label>Email</Form.Label>
                <Form.Control value={email} onChange={e=>setEmail(e.target.value)} />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" value={pw} onChange={e=>setPw(e.target.value)} />
              </Form.Group>
              <Button type="submit" className="w-100">Login</Button>
            </Form>
          </Card>
        </Col>
      </Row>
    </Container>
  )
}
